FlagParam.prototype.set = function(value){
  var current = read(this.target)
  var val = Array.isArray(current) ? current.slice() : []

  var index = val.indexOf(this.flag)
  var currentState = !!~index
  var newState = !!value

  if (newState !== currentState){
    if (newState){
      val.push(this.flag)
    } else {
      val.splice(index, 1)
    }
  }

  this.target.set(val)
}

