function getRatio(value, min, max, curve, mag){

  mag = mag || 1

  if (curve === 'log'){
    min = Math.log(min*mag)/Math.log(10)
    max = Math.log(max*mag)/Math.log(10)
    value = Math.log(value*mag)/Math.log(10)
  } else if (curve === 'log+'){
    min = Math.log(min*mag+1)/Math.log(10)
    max = Math.log(max*mag+1)/Math.log(10)
    value = Math.log(value*mag+1)/Math.log(10)
  } else if (curve === 'db'){
    min = gainToDecibels(min)
    max = gainToDecibels(max)
    value = gainToDecibels(value)
  } else if (curve === 'exp'){
    min = Math.pow(min, 1/(2*mag))
    max = Math.pow(max, 1/(2*mag))
    value = Math.pow(value, 1/(2*mag))
  }

  value = value - min

  var range = max - min
  return value / range
}

function getValueFromScreen(offset, start, min, max, curve, mag){
  mag = mag || 1

  if (curve === 'log'){
    min = Math.log(min*mag)/Math.log(10)
    max = Math.log(max*mag)/Math.log(10)
    start = Math.log(start*mag)/Math.log(10)
  } else if (curve === 'log+'){
    min = Math.log(min*mag+1)/Math.log(10)
    max = Math.log(max*mag+1)/Math.log(10)
    start = Math.log(start*mag+1)/Math.log(10)
  } else if (curve === 'db'){
    min = Math.max(-40, gainToDecibels(min))
    max = gainToDecibels(max)
    start = Math.max(-40, gainToDecibels(start))
  } else if (curve === 'exp'){
    min = Math.pow(min, 1/(2*mag))
    max = Math.pow(max, 1/(2*mag))
    start = Math.pow(start, 1/(2*mag))
  }

  start = start - min

  var range = max - min
  var value = start + (range * offset)

  if (curve === 'log'){
    value = Math.pow(10, value + min) / mag
  } else if (curve === 'log+'){
    value = (Math.pow(10, value + min) - 1) / mag
  } else if (curve === 'db'){
    value = decibelsToGain(value + min)
  } else if (curve === 'exp'){
    value = Math.pow(value, 2*mag)
  } else {
    value += min
  }

  return value
}

function round(value, dp){
  var pow = Math.pow(10, dp || 0)
  return Math.round(value * pow) / pow
}

module.exports = function getClosestPoint(markers, time) {
  if (markers && markers.length) {
    var prev = 0
    for (var i = 0; i < markers.length; i++) {
      var marker = markers[i]//-0.02
      if (time === marker) {
        return time
      } else if (marker > time) {
        var diff = marker - time
        var prevDiff = time - prev
        if (diff > prevDiff) {
          return prev
        } else {
          return marker
        }
      }
      prev = marker
    }
  }
  return time
}

module.exports = getPortSiblings

function getPortSiblings (obs, controllers) {
  var index = null
  var matches = []
  controllers.forEach(function (c, i) {
    if (c.port && c.port() === obs.port()) {
      if (c === obs) {
        index = i
      }
      matches.push(i)
    }
  })
  return [
    controllers.get(matches[mod(index - 1, matches.length)]),
    controllers.get(matches[mod(index + 1, matches.length)])
  ]
}


function mod (n, m) {
  return ((n % m) + m) % m
}

module.exports = function getSoundOffset(buffer){

  if (!buffer) return

  var threshold = 0.01

  var data = buffer.getChannelData(0)
  var step = 32
  var width = buffer.length / step

  for(var i=0;i<width;i++){
    var min = 1.0
    var max = -1.0
    for (j=0; j<step; j++) {
      var datum = data[(i*step)+j]
      if (datum < min){
        min = datum
      }
      if (datum > max){
        max = datum
      }
    }

    if (Math.max(Math.abs(min), Math.abs(max)) > threshold){
      var value = (i*step) / buffer.length
      return [value, 1]
    }
  }
}

