function renderRows (controller, rows, cols) {
  var children = []
  for (var r = 0; r < rows; r++) {
    var buttons = []
    for (var c = 0; c < cols; c++) {
      buttons.push(h('div.button', {
        'ev-dragenter': MPE(enterButton, controller),
