        var id = parseInt(key, 10)
        var rgb = currentState[key] || off
        if (Array.isArray(rgb)) {
          toUpdate.push(id, format(rgb[0]), format(rgb[1]), format(rgb[2]))
        } else {
          toPulse.push(0, id, rgb)
        }
