function InputStack(){

  var keysDown = null
  var stack = []
  var release = null

  function grab(){
    var input = this
    var index = stack.indexOf(input)
    if (!stack.length || index < stack.length - 1){

      release&&release()

      if (stack.length){
        stack[stack.length-1].set([])
      }

      if (~index){
        stack.splice(index, 1)
      }
      stack.push(input)
      release = activate(input)
    }
  }

  function close(){
    var input = this

    if (stack.length){
      var index = stack.indexOf(input)
      if (index === stack.length-1){
        release&&release()
        stack.pop()
        release = activate(stack[stack.length-1])
      }
    }

    input.set([])
  }

  function activate(input) {
    if (input) {
      var releaseInput = watch(getKeysDown(), input.set)
      input.active.set(true)

      return function() {
        releaseInput()
        input.active.set(false)
      }
    }
  }

  function next(){
    if (stack.length > 1){
      var input = stack.pop()
      release&&release()
      input.set([])
      input.active.set(false)

      stack.unshift(input)
      release = activate(stack[stack.length-1])
    }
  }

  function swap(){
    if (stack.length > 1){
      var input = stack.pop()
      release&&release()
      input.set([])
      input.active.set(false)
      stack.splice(-1, 0, input)
      release = activate(stack[stack.length-1])
    }
  }

  function getKeysDown(){
    return keysDown = keysDown || KeysDown()
  }

  function handleKey(e) {
    var el = document.activeElement
    if (stack.length) {
      if (!el || (!shouldIgnore(el) && el.contentEditable !== 'true')){
        if ((e.code === 'Tab' || e.code === 'Enter') && e.type === 'keydown') { // tab or enter
          e.preventDefault()
          next()
        } else if (e.code === 'AltLeft' || e.code === 'AltRight') { // alt
          e.preventDefault()
          swap()
        }
      }
    }
  }

  document.addEventListener('keydown', handleKey, false)
  document.addEventListener('keyup', handleKey, false)

  return function(){
    var input = Observ([])
    input.active = Observ()
    input.grab = grab.bind(input)
    input.close = close.bind(input)
    input.grab()
    return input
  }

