        ['150%', 1.5]
      ]
    })
  ])

  return h('TabbedEditor', [
    h('header', [project.items._list.map(renderTab, project), controls]),
    tabs.length ? tabs : renderHelper()
  ])
}

function renderTab(item){
  var project = this
  var actions = project.actions

  var data = item&&item()
  if (data){
    var selected = project.selected() == item.path
    return h('div.tab', {
      'ev-click': send(project.selected.set, item.path),
      'className': selected ? '-selected' : null
    }, [
      h('span', [getName(item.path)]),
      h('button.close', {'ev-click': send(actions.closeFile, item.path)})
    ])
  } else {
    return ''
  }
}

function getName(path){
  var base = getBaseName(path, '.json')
  if (base === 'index'){
    return getBaseName(getDirectory(path))
  } else {
    var dir = getBaseName(getDirectory(path))
    return dir + ' > ' + base
  }
}

function renderHelper(){
  return h('div CenterTab', [
    h('div Helper', [
      h('a', {href: 'http://loopjs.com/'}, [
        h('img', {src: 'file://' + join(__dirname, '..', '..', 'logo.png'), width: 128})
      ]),
      h('br'),
      'For help visit ',
      h('a', {href: 'http://loopjs.com/'}, 'loopjs.com')
    ])
  ])
}

function getItem(state){
  var result = null
  var path = state.selected()
  state.items.some(function(item){
    if (item.path === path){
      result = item
      return true
    }
  })
  return result
}

var VirtualDom = require('virtual-dom')
var MainLoop = require('main-loop')
var h = require('micro-css/h')(require('virtual-dom/h'))

var Observ = require('observ')
var nextTick = require('next-tick')
var watch = require('observ/watch')

var renderNode = require('lib/render-node')

module.exports = Editor

function Editor(fileObject){
  if (!(this instanceof Editor)) return new Editor(fileObject)
  this.fileObject = fileObject
}

Editor.prototype.type = 'Widget'

Editor.prototype.init = function(){

  var state = this.state = {
    releases: [],
    fileObject: this.fileObject,
    update: update
  }

  var pendingUpdate = false

  function update(){
    if (!pendingUpdate){
      pendingUpdate = true
      nextTick(doUpdate)
    }
  }

  function doUpdate(){
    pendingUpdate = false

    // HACK: schedule 300 ms ahead to avoid audio interuption
    //state.fileObject.context.scheduler.schedule(0.3)
    loop.update()
  }

  var loop = MainLoop(null, function(){
    if (state.fileObject && state.fileObject.node){
      return renderNode(state.fileObject.node)
    } else {
      return h('div')
    }
  }, VirtualDom)

  bindToObject(this, update)

  return loop.target
}

Editor.prototype.update = function(prev, elem){
  var state = this.state = prev.state
  state.fileObject = this.fileObject

  if (this.fileObject !== prev.fileObject){
    bindToObject(this, state.update)
    state.update()
  }
}

Editor.prototype.destroy = function(elem){
  var state = this.state
  state.releases.forEach(invoke)
  state.releases.length = 0
}

function bindToObject(self, update){
  var state = self.state
  var fileObject = self.fileObject

  state.releases.forEach(invoke)
  state.releases.length = 0

  if (fileObject){
    state.releases.push(
      fileObject.onNode(update),
      fileObject(update)
    )
    if (fileObject.resolved){
      state.releases.push(
        fileObject.resolved(update)
      )
    }
  }
}

function invoke(func){
  return func()
}

var JSMN = require('lib/jsmn.js')
var deepEqual = require('deep-equal')
var watch = require('observ/watch')

var ace = require('brace')
require('brace/mode/javascript')
require('brace/theme/ambiance')

var NO_TRANSACTION = {}

module.exports = RawEditor

function RawEditor(fileObject){
  if (!(this instanceof RawEditor)){
    return new RawEditor(fileObject)
  }
  this.fileObject = fileObject
  this.file = fileObject && fileObject.file
}

RawEditor.prototype.type = 'Widget'

RawEditor.prototype.init = function(){
  var element = document.createElement('div')
  element.className = 'RawEditor'

  var el = document.createElement('div')

  var textEditor = this.editor = ace.edit(el)

  window.editors = window.editors || []
  window.editors.push(textEditor)

  textEditor.setTheme('ace/theme/ambiance');
  textEditor.session.setMode('ace/mode/javascript')
  textEditor.session.setUseWorker(false)
  textEditor.session.setTabSize(2)
  textEditor.renderer.setScrollMargin(20,100)
  textEditor.renderer.setPadding(20)
  textEditor.renderer.setShowGutter(false)

  var currentFile = null
  var self = this

  var currentTransaction = NO_TRANSACTION
  var lastSave = NO_TRANSACTION

  textEditor.setFile = function(fileObject){
    clearTimeout(saveTimer)

    if (self.release){
      self.release()
      self.release = null
    }

    currentFile = fileObject

    if (fileObject){
      self.release = watch(fileObject, update)
    }
  }

  function save(){
    var value = textEditor.session.getValue()
    if (currentFile){
      try {
        var object = JSMN.parse(value)
        lastSave = object
        currentFile.set(object)
      } catch (ex) {}
    }
  }

  function update(){
    var data = currentFile ? currentFile() : null
    if (data && !deepEqual(lastSave, data)) {
      var newValue = JSMN.stringify(data || {})
      currentTransaction = newValue
      textEditor.session.setValue(newValue, -1)
      currentTransaction = NO_TRANSACTION
    }
  }

  var blurTimer = null
  textEditor.on('focus', function(){
    clearTimeout(blurTimer)
  })

  textEditor.on('blur', function(){
    clearTimeout(blurTimer)
    blurTimer = setTimeout(function(){
      if (!textEditor.isFocused()){
        update()
      }
    }, 100)
  })

  var saveTimer = null
  textEditor.on('change', function(){
    if (currentTransaction === NO_TRANSACTION){
      clearTimeout(saveTimer)
      saveTimer = setTimeout(save, 100)
    }
  })

  textEditor.setFile(this.fileObject)

  element.appendChild(el)
  return element
}

RawEditor.prototype.update = function(prev, elem){
  this.editor = prev.editor
  this.release = prev.release

  if (prev.file !== this.file){
    this.editor.setFile(this.fileObject)
  }
  return elem
}

RawEditor.prototype.destroy = function(elem){
  this.editor.destroy()
  this.release && this.release()
  this.release = null
}

var h = require('micro-css/h')(require('virtual-dom/h'))
var send = require('value-event/event')

var MPE = require('lib/mouse-position-event.js')
var nextTick = require('next-tick')
var importSample = require('lib/import-sample')
var extend = require('xtend/immutable')

module.exports = SlotChooser

function SlotChooser(chunk, spawnSlot){

  var triggers = []
  var shape = chunk.shape() || [1,1]
  var selectedSlotId = chunk.selectedSlotId()
  var slots = chunk.context.slotLookup
  var length = shape[0] * shape[1]

  for (var i=0;i<length;i++){
    var id = String(i)
    var slot = slots.get(id)
    var width = (100 / shape[1]) + '%'
    var dragInfo = { collection: chunk.slots, id: id, select: chunk.selectedSlotId.set, spawnSlot: spawnSlot, chunk: chunk }

    if (slot){
      triggers.push(
        h('div.slot', {
          'draggable': true,

          'ev-dragstart': MPE(dragStart, dragInfo),
          'ev-dragend': MPE(dragEnd, dragInfo),
          'ev-dragover': MPE(dragOver, dragInfo),
          'ev-dragleave': MPE(dragLeave, dragInfo),
          'ev-drop': MPE(drop, dragInfo),

          'style': {width: width},
          'className': selectedSlotId === id ? '-selected' : '',
          'ev-click': send(chunk.selectedSlotId.set, id)
        }, [
          id,
          h('button.remove', {
            'ev-click': send(chunk.slots.remove, slot),
          }, 'X')
        ])
      )
    } else {
      triggers.push(
        h('div.slot -spawn', {
          'style': {width: width},

          'ev-dragover': MPE(dragOver, dragInfo),
          'ev-dragleave': MPE(dragLeave, dragInfo),
          'ev-drop': MPE(drop, dragInfo),

          'ev-click': send(spawnSlot, { id: id, chunk: chunk })
        }, '+ trigger')
      )
    }


  }

  return h('SlotChooser', [
    triggers,
    h('div.spacer'),
    h('div.slot -output', {
      'className': selectedSlotId === 'output' ? '-selected' : '',
      'ev-click': send(chunk.selectedSlotId.set, 'output')
    }, 'output')
  ])
}

var currentDrag = null

function dragStart(ev){
  currentDrag = ev.data
}

function dragEnd(ev){
  currentDrag = null
}

function dragOver(ev){
  ev.currentTarget.classList.add('-dragOver')
  if (ev.altKey || containsFiles(ev.dataTransfer)) {
    ev.dataTransfer.dropEffect = 'copy'
  } else {
    ev.dataTransfer.dropEffect = 'move'
  }
  ev.event.preventDefault()
}

function drop(ev){
  dragLeave(ev)
  ev.event.preventDefault()

  var targetCollection = ev.data.collection
  var targetLookup = targetCollection.context.slotLookup
  var target = targetLookup.get(ev.data.id)

  if (containsFiles(ev.dataTransfer) || ev.dataTransfer.types.includes('loop-drop/sample-path')) {
    var path = ev.dataTransfer.items[0].kind === 'file'
      ? ev.dataTransfer.items[0].getAsFile().path
      : ev.dataTransfer.getData('loop-drop/sample-path')

    if (target){
      targetCollection.remove(target)
    }

    var node = targetCollection.push({
      id: ev.data.id,
      node: 'slot',
      output: 'output',
      sources: [
        { node: 'source/sample', mode: 'oneshot' }
      ]
    })

    importSample(targetCollection.context, path, function (err, descriptor) {
      var player = node.sources.get(0)
      player.set(extend(player(), descriptor))
      ev.data.select(ev.data.id)
    })
  } else if (ev.dataTransfer.types.includes('loop-drop/source')) {
    var data = JSON.parse(ev.dataTransfer.getData('loop-drop/source'))
    if (!target) {
      target = ev.data.spawnSlot({id: ev.data.id, chunk: ev.data.chunk})
    }
    target.sources.push(data)
  } else if (ev.dataTransfer.types.includes('loop-drop/processor')) {
    var data = JSON.parse(ev.dataTransfer.getData('loop-drop/processor'))
    if (!target) {
      target = ev.data.spawnSlot({id: ev.data.id, chunk: ev.data.chunk})
    }
    target.processors.push(data)
  } else {
    var sourceCollection = currentDrag.collection
    var sourceLookup = sourceCollection.context.slotLookup
    var source = sourceLookup.get(currentDrag.id)
    var isCopying = ev.altKey

    if (source && source !== target){

      if (target){
        // clear out existing
        targetCollection.remove(target)
      }

      if (sourceCollection !== targetCollection || isCopying){

        // move to different collection
        var descriptor = obtainWithId(ev.data.id, source())

        if (!isCopying){
          sourceCollection.remove(source)
        }

        targetCollection.push(descriptor)

      } else {
        source.id.set(ev.data.id)
      }

      ev.data.select(ev.data.id)
    }
  }
}

function dragLeave(ev){
  ev.currentTarget.classList.remove('-dragOver')
}

function obtainWithId(id, obj){
  obj = JSON.parse(JSON.stringify(obj))
  obj.id = id
  return obj
}

function containsFiles(transfer) {
  if (transfer.types) {
    for (var i = 0; i < event.dataTransfer.types.length; i++) {
      if (event.dataTransfer.types[i] == "Files") {
        return true
      }
    }
  }
  return false
}
